mapper.select:

select *
  from ( 
    select rownum num, A.* 
      from ( 
        select * 
          from gameshop
         order by gDate desc) A 
     where rownum <= #{param2} ) B 
 where B.num >= #{param1}


select *
  from ( 
    select rownum num, A.* 
      from ( 
        select * 
          from gameshop
         where cname like #{param1} order by cid desc) A 
     where rownum <= #{param3}) B 
 where B.num >= #{param2}


select *
  from ( 
    select rownum num, A.* 
      from ( 
        select * 
          from gameshop
         where kind = #{param1} order by gdate desc) A 
     where rownum <= #{param3}) B 
 where B.num >= #{param2}

select avg(rscore) as avgscore 
  from shopreview
 where cid = #{param1}

mapper.update:
update gameshop 
   set rcount = rcount + 1 
 where cid = #{cId}

update gameshop 
   set rcount = rcount - 1 
 where cid = #{cId}

mapper.select:
		select * from shopreview
		 where cid = ${param}
      order by rdate desc

mapper.update:
update shopreview 
   set rscore = #{param3}, rcontent = #{param4}, rmodi = 1
 where rid = #{param5}

mapper.select:
select count(*) as total 
  from shopreview 
 where mid = #{param1}


select *
  from ( 
    select rownum num, A.* 
      from ( 
        select b.rid, a.cid, a.cname, b.rcontent, b.rdate 
		  from gameshop a, shopreview b 
		 where a.cid = b.cid and b.mid = #{param1} order by a.cid desc) A 
     where rownum <= #{param3}) B 
 where B.num >= #{param2}

mapper.update:
update gameshop 
   set cname = #{param2}, cPrice = #{param3}, cCount = #{param4}, cSale = #{param3} - ((#{param3}) * (#{param5} / 100)), cDown = #{param5}
 where cid = #{param1}
